class Departamentomodel extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get departamento by id
     */
    function get_departamento($id)
    {
        return $this->db->get_where('departamento',array('id'=>$id))->row_array();
    }
    
    /*
     * Get all listadepartamento
     */
    function get_all_listadepartamento()
    {
        return $this->db->get('departamento')->result_array();
    }
    
    /*
     * function to add new departamento
     */
    function add_departamento($params)
    {
		$params['id'] = $this->get_idConsecutivo();
        $this->db->insert('departamento',$params);
        return $this->db->insert_id();
    }
    
    function get_idConsecutivo()
    {
		$maxid = 1;
        $row = $this->db->query("select max(id) as 'maxid' from departamento")->row();
		if ($row) {
			$maxid = $row->maxid + 1;
		}
		return $maxid;
	}
    
    /*
     * function to update departamento
     */
    function update_departamento($id,$params)
    {
        $this->db->where('id',$id);
        $response = $this->db->update('departamento',$params);
        if($response)
        {
            return "Departamento actualizado exitosamente";
        }
        else
        {
            return "Ocurrió un error al actualizar el departamento";
        }
    }
    
    /*
     * function to delete departamento
     */
    function delete_departamento($id)
    {
        $response = $this->db->delete('departamento',array('id'=>$id));
        if($response)
        {
            return "Departamento eliminado exitosamente";
        }
        else
        {
            return "Ocurrió un error al actualizar el departamento";
        }
    }
}
