    function get_numero_oficioConsecutivo($elaboracionYear){
        $this->db->select('numOficio');
        $this->db->from('po_numoficio');
        $this->db->where('anio', $elaboracionYear);
        $this->db->order_by('numOficio', 'DESC');
        $this->db->limit(1);
        $query = $this->db->get();
        if($query->num_rows() > 0){
            return $query->row('numOficio') + 1;
        } else {
            return 0;
        }

    }

