    function get_year_maximo($elaboracionYear){
        $maxyear = $elaboracionYear;
        $row = $this->db->query("select max(anio) as 'maxanio' from po_numoficio")->row();
        if ($row) {
			$maxyear = $row->maxanio;
		}
        return $maxyear;
    }

